let x = 30.2862;
let y = -97.7394;
const down = (
  <svg
    xmlns="http://www.w3.org/2000/svg"
    fill="none"
    viewBox="0 0 24 24"
    strokeWidth={1.5}
    stroke="currentColor"
    className="h-6 w-6"
  >
    <path
      strokeLinecap="round"
      strokeLinejoin="round"
      d="M19.5 8.25l-7.5 7.5-7.5-7.5"
    />
  </svg>
);

const up = (
  <svg
    xmlns="http://www.w3.org/2000/svg"
    fill="none"
    viewBox="0 0 24 24"
    strokeWidth={1.5}
    stroke="currentColor"
    className="h-6 w-6"
  >
    <path
      strokeLinecap="round"
      strokeLinejoin="round"
      d="M4.5 15.75l7.5-7.5 7.5 7.5"
    />
  </svg>
);

const Home: NextPage = () => {
  const [places, setPlaces] = useState([])
  const { data: session } = useSession();
  const centerRef = useRef<[number, number]>([x, y]);
  const [rating, setRating] = useState(0);
  const [currentPlace, setCurrentPlace] = useState(null);
  const [toggle, setToggle] = useState(false);
  const [reload, setReload] = useState(false);
  const [commentToggle, setCommentToggle] = useState(false);

  function addNewPlace(placeInfo: any) {
    places.push(placeInfo);
  }

  function postRating() {
    if (rating == 0) {
      return 
    }
    //Check ternary statement (Similar to comments property)
    setReload((prev) => !prev);
    axios.put(process.env.NEXT_PUBLIC_ENV_LOCAL_VARIABLE + 'location', {
      'rating': rating, 'name': currentPlace ? currentPlace.name: ''
    })
    .then(() => {setCurrentPlace((prev) => { 
      var copy = prev;
      copy.total_rating += rating;
      copy.count += 1;
      return copy;
     })})
    //Update rating
    setRating(0)
  }
  

  useEffect(() => {
    axios.get(process.env.NEXT_PUBLIC_ENV_LOCAL_VARIABLE).then((res) => {
      setPlaces(res.data.locations)
    })
  }, [reload])

  if (session) {
    return (
      <>
        <Head>
          <title>Water Fountain Spots</title>
        </Head>
        <div className="relative h-screen w-screen overflow-hidden">
          <Map
            center={centerRef.current}
            zoom={17}
            onBoundsChanged={({ center }) => {
              centerRef.current = center;
            }}
          >
            {places.map((place) => (
              <Marker
                key={place.name}
                anchor={[place.x, place.y]}
                width={50}
                className="w-full"
                onClick={() => {
                  setToggle(true);
                  setCurrentPlace(place);
                  centerRef.current = [place.x, place.y];
                }}
              />
            ))}
          </Map>
          
          <div className="absolute top-0 z-10 flex h-full w-1/3 flex-col gap-3 overflow-y-auto bg-slate-800 p-8 text-white">
            <h2 className="text-3xl font-bold uppercase">
              {currentPlace?.name}
            </h2>
            <h3 className="italic">{currentPlace?.category}</h3>
            <img
              className=" aspect-[2] w-full object-cover font-bold"
              src={currentPlace?.image}
              alt={currentPlace?.name}
            />
            <p className="text-md text-le(ft w-full font-bold">
              Rating: {currentPlace?.total_rating/currentPlace?.count}
            </p>

            <div className="flex w-full items-center justify-between">
              {[1, 2, 3, 4, 5].map((num) => (
                <button
                  key={num}
                  className={`aspect-square w-[10%] rounded-full bg-black ${
                    rating === num ? "bg-slate-600" : ""
                  }`}
                  onClick={() => {
                    setRating(num);
                  }}
                >
                  {num}
                </button>
              ))}
              {/* submit rating */}
              <button
                className="rounded-2xl bg-slate-400 p-2 hover:bg-slate-600 transition duration-200"
                onClick={() => postRating()}
              >
                Submit
              </button>
            </div>

            <p className="text-sm overflow-y-auto h-60">{currentPlace?.description}</p>
            <div
              onClick={() => setCommentToggle((p) => !p)}
              className="flex w-full gap-4 hover:cursor-pointer"
            >
              <h3 className="text-md w-full text-left">Comments</h3>
              {commentToggle ? up : down}
            </div>
            {commentToggle && (
              <ul className="w-full overflow-y-auto">
                {currentPlace?.comments["Comments"].map((comment: any) => (
                  <li className="flex flex-row" key={comment.comment}>
                    <p className="text-left text-sm">{comment.user.name}</p>
                    &nbsp;&nbsp;
                    <p className="text-left text-sm italic">
                      {comment.comment}
                    </p>
                  </li>
                ))}
              </ul>
            )}

            {/*Look back at this, fix initial place*/}
            <Comment place = {currentPlace ? currentPlace.name: null} name = {session.user}/>
          </div>
          <div className="absolute right-5 top-5">
            {/* signout */}
            <button
              onClick={() => signOut()}
              className="rounded-md bg-slate-800 p-2 text-white"
            >
              Sign Out
            </button>
          </div>
        </div>
      </>
    );
  } else {
    return (
      <div className="flex h-screen w-screen flex-col items-center justify-center bg-slate-800 text-white">
        <p className="italic">
          Only @utexas.edu emails can be used to sign in.{" "}
        </p>
        <button
          className="bold flex flex-row items-center justify-center gap-10 rounded-2xl border-2 px-10 py-2 text-2xl hover:border-blue-400 hover:text-blue-400"
          onClick={() => signIn("google")}
        >
          <img src="/google.png" className="aspect-square w-14"></img>
          Sign In with Google
        </button>
      </div>
    );
  }
};

export default Home;
